  ```less
  @import "./variable.less";
  @import "./dep.less";

  @moye-textbox-background-color: #fff;
  @moye-textbox-text-color: #333;

  .@{moye-prefix}-textbox {
    background-color: @moye-textbox-background-color;
    color: @moye-textbox-text-color;
  }

